$(document).ready(function () {
  // scroll-container with swiper
  if (window.innerWidth >= 1025) { 
    var termSwiper = new Swiper(".termSwiper", {
      direction: "vertical",
      slidesPerView: "auto",
      freeMode: true,
      scrollbar: {
        el: ".swiper-scrollbar",
      },
      mousewheel: true,
    });
    var refundSwiper = new Swiper(".refundSwiper", {
      direction: "vertical",
      slidesPerView: "auto",
      freeMode: true,
      scrollbar: {
        el: ".swiper-scrollbar",
      },
      mousewheel: true,
    });
    var privacySwiper = new Swiper(".privacySwiper", {
      direction: "vertical",
      slidesPerView: "auto",
      freeMode: true,
      scrollbar: {
        el: ".swiper-scrollbar",
      },
      mousewheel: true,
    });
  }

  // services
  $('.services__item button').on('click', function () {
    var serviceName = $(this).parent().find('h2').text();
    $('#service_name').val(serviceName);
    $('.order-details-name').text(serviceName);

    var radioBtn = $(this).siblings('.radio__btns').find('input[type=radio]:checked').parent();
    var serviceKolvo = radioBtn.find('span:first').text();
    var servicePrice = radioBtn.find('span:last').text();

    $('#service_kolvo').val(serviceKolvo);
    $('#service_price').val(servicePrice);

    $('.order-details-charact span:first').text(serviceKolvo);
    $('.order-details-charact span:last').text(servicePrice);
  });

  // modals close and open for submit order form
  document.addEventListener( 'wpcf7mailsent', function( event ) {
    $.fancybox.close({
      src  : '#modal-order',
      type : 'inline'
    });
    $.fancybox.open({
      src  : '#modal-order-yes',
      type : 'inline'
    });
  }, false );

});